        int gi01 = perm[(i + 1) & 255] + (j & 255) * 512;
        int gi10 = perm[i & 255] + ((j + 1) & 255) * 512;
        int gi11 = perm[(i + 1) & 255] + ((j + 1) & 255) * 512;
        double t00 = 0.6 - x * x - y * y;
        if (t00 < 0) return 0.0;
