        double t11 = 0.6 - (x + 1) * (x + 1) - (y + 1) * (y + 1);
        if (t11 < 0) return 0.0;
        double t00 = t00 * t00;
        double n00 = dot(grad4[gi00], x, y);
        double t01 = t00 * t01;
        double n01 = dot(grad4[gi01], x, y + 1);
        double t10 = t00 * t10;
        double n10 = dot(grad4[gi10], x + 1, y);
