public void update() {
	if (oldPos != null) { // if the old position is not null, we need to check if it has changed since last update
		if (!oldPos.equals(pos)) { // if the position has changed since last update, we set the old position to the current position
			oldPos.set(pos);
		}
		if (!oldRot.equals(rot)) { // if the rotation has changed since last update, we set the old rotation to the current rotation
			oldRot.set(rot);
		}
		if (!oldScale.equals(scale)) { // if the scale has changed since last update, we set the old scale to the current scale
			oldScale.set(scale);
		}
	} else { // if the old position is null, we create new objects for each of them and set them to the current values
		oldPos = new Vector3f().set(pos);
		oldRot = new Quaternion().set(rot);
		oldScale = new Vector3f().set(scale);
	}
}

