public class Chunk {
    public static final int CHUNK_SIZE = 16;
    public static final int CHUNK_SIZE_SQUARED = CHUNK_SIZE * CHUNK_SIZE;
    public static final int CHUNK_SIZE_CUBED = CHUNK_SIZE * CHUNK_SIZE_SQUARED;

    private Block[] blocks = new Block[CHUNK_SIZE_CUBED];

    public void updateBlocks() {
        for (int i = 0; i < CHUNK_SIZE_CUBED; i++) {
            if (blocks[i] != null) {
                int n_x = i - 1;
                int p_x = i + 1;
                int n_y = i - CHUNK_SIZE;
                int p_y = i + CHUNK_SIZE;
                int n_z = i - CHUNK_SIZE_SQUARED;
                int p_z = i + CHUNK_SIZE_SQUARED;

                if (blocks[i].x - 1 < 0) {
                    //TODO: check neighbor chunk
                    blocks[i].lt = false;
                } else {
                    Block bl = blocks[n_x];
                    if (bl == null)
                        blocks[i].lt = true;
                    else
                        blocks[i].lt = false;
                }
                if (blocks[i].y - 1 < 0) {
                    //TODO: check neighbor chunk
                    blocks[i].bt = false;
                } else {
                    Block bl = blocks[n_y];
                    if (bl == null)
                        blocks[i].bt = true;
                    else
                        blocks[i].bt = false;
                }
                if (blocks[i].z - 1 < 0) {
                    //TODO: check neighbor chunk
                    blocks[i].ft = false;
                } else {
                    Block bl = blocks[n_z];
                    if (bl == null)
                        blocks[i].ft = true;
                    else
                        blocks[i].ft = false;
                }

                if (blocks[i].x + 1 >= CHUNK_SIZE) {
                    //TODO: check neighbor chunk
                    blocks[i].rt = false;
                } else {
                    Block bl = blocks[p_x];
                    if (bl == null)
                        blocks[i].rt = true;
                    else
                        blocks[i].rt = false;
                }
                if (blocks[i].y + 1 >= CHUNK_SIZE) {
                    //TODO: check neighbor chunk
                    blocks[i].tp = false;
                } else {
                    Block bl = blocks[p_y];
                    if (bl == null)
                        blocks[i].tp = true;
                    else
                        blocks[i].tp = false;
                }
                if (blocks[i].z + 1 >= CHUNK_SIZE) {
                    //TODO: check neighbor chunk
                    blocks[i].bk = false;
                } else {
                    Block bl = blocks[p_z];
                    if (bl == null)
                        blocks[i].bk = true;
                    else
                        blocks[i].bk = false;
                }
            }
        }
    }
}

