        int gi00 = perm[i & 255] + (j & 255) * 512;
        int gi01 = perm[(i + 1) & 255] + (j & 255) * 512;
        int gi10 = perm[i & 255] + ((j + 1) & 255) * 512;
        int gi11 = perm[(i + 1) & 255] + ((j + 1) & 255) * 512;
        double t00 = 0.6 - x * x - y * y;
        if (t00 < 0) return 0.0;
        double t10 = 0.6 - (x + 1) * (x + 1) - y * *;
        if (t10 < 0) return 0.0;
        double t01 = 0.6 - x * x - (y + 1) * (y + 1);
        if (t01 < 0) return 0.0;
        double t11 = 0.6 - (x + 1) * (x + 1) - (y + 1) * (y + 1);
        if (t11 < 0) return 0.0;
        double t00 = t00 * t00;
        double n00 = dot(grad4[gi00], x, y);
        double t01 = t00 * t01;
        double n01 = dot(grad4[gi01], x, y + 1);
