private void gen() {
    for (int i = 0; i < W; i++) {
        for (int j = 0; j < H; j++) {
            for (int k = 0; k < D; k++) {
                chunks[i][j][k] = new Chunk(i - W / 2 + x, j - H / 2 + y, k - D / 2 + z);
                chunks[i][j][k].updateBlocks();
                chunks[i][j][k].toGenModel();
            }
        }
    }
